mapper.sql:
FROM codeGroup a
WHERE 1=1
	AND deleteNy = #{shDelNy}


		AND a.creationDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



		AND a.seq = #{shValue}
		AND a.codeGroupName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE codeGroup 
SET
	codeGroupName = #{codeGroupName}
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
DELETE FROM codeGroup
WHERE 1=1
	AND seq = #{seq}
