mapper.sql:
	FROM codeGroup a
	LEFT JOIN code b 
         on b.cgSeq = a.cgSeq
         WHERE 1=1
	        AND b.cdDeleteNy = #{shDelNy}


				AND b.cdCreationDate BETWEEN #{shDateStart} AND #{shDateEnd}
				AND b.cdModificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



	            AND b.cdSeq = #{shValue}
	            AND b.cdName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE code
SET
	cdName = #{cdName}
WHERE 1=1
	AND cdSeq = #{cdSeq}

mapper.delete:
DELETE FROM code
WHERE 1=1
	AND cdSeq = #{cdSeq}
