mapper.sql:
	FROM codeGroup a
	LEFT JOIN code b 
         on b.codeGroup_seq = a.seq
         WHERE 1=1
	        AND b.deleteNy = #{shDelNy}


				AND b.creationDate BETWEEN #{shDateStart} AND #{shDateEnd}
				AND b.modificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



	            AND b.seq = #{shValue}
	            AND b.codeName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE code
SET
	codeName = #{codeName}
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
DELETE FROM code
WHERE 1=1
	AND seq = #{seq}
