mapper.sql:
     FROM member a
        WHERE 1=1
        AND a.deleteNy = #{shDelNy}


			AND a.creationDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.modificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.seq = #{shValue}
            AND a.name LIKE CONCAT('%',#{shValue},'%')

mapper.update:
	UPDATE member 
	SET
		name = #{name}
		,dateBirth = #{dateBirth}
        ,gender = #{gender}
        ,email = #{email}
        ,password = #{password}
        ,phoneNumber = #{phoneNumber}
	WHERE 1=1
		AND seq = #{seq}

mapper.delete:
DELETE FROM member
WHERE 1=1
	AND seq = #{seq}
