mapper.sql:
FROM member a JOIN payment b on b.mbSeq = a.mbSeq JOIN productpayment c on c.pmSeq = b.pmSeq JOIN product d on d.pdSeq = c.pdSeq WHERE a.mbSeq = #{mbSeq}
AND pmCreationDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND d.pdName LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT COUNT(*)
FROM address
WHERE mbSeq = #{mbSeq}

mapper.update:
UPDATE address
SET
arName = #{arName}
,arUserName = #{arUserName}
,arPhoneNumber = #{arPhoneNumber}
,arPostalCode = #{arPostalCode}
,arNumberAddress = #{arNumberAddress}
,arRoadNameAddress = #{arRoadNameAddress}
,arDetailAddress = #{arDetailAddress}
WHERE 1=1
AND arSeq = #{arSeq}

UPDATE member
SET
mbName = #{mbName}
,mbDateBirth = #{mbDateBirth}
,mbGender = #{mbGender}
,mbPhoneNumber = #{mbPhoneNumber}
WHERE 1=1
AND mbEmail = #{mbEmail}

UPDATE member
SET
mbPassword = #{newmbPassword}
WHERE 1=1
AND mbSeq = #{mbSeq}
