mapper.sql:
FROM product
WHERE 1=1
	AND pdDeleteNy = #{shDelNy}


		AND pdCreationDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND pdModificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



		AND pdSeq = #{shValue}
		AND pdName LIKE CONCAT('%',#{shValue},'%')

FROM product
WHERE 1=1
	AND pdDeleteNy = 0

		AND pdSeq = #{shValue}
		AND pdName LIKE CONCAT('%',#{shValue},'%')
		AND pdCode = #{pdCode}

FROM product a
left join size b on b.pdSeq = a.pdSeq
left join color c on c.pdSeq = a.pdSeq
WHERE 1=1
	AND pdDeleteNy = 0 


		a.pdCode IN

		           #{item}




		AND a.pdGender IN

		           #{item}




		AND b.szCode IN

		           #{item}




		AND c.clCode IN

		           #{item}

mapper.update:
UPDATE product 
SET
	pdName = #{pdName}
WHERE 1=1
	AND pdSeq = #{pdSeq}

mapper.delete:
DELETE FROM product
WHERE 1=1
	AND pdSeq = #{pdSeq}
