mapper.sql:
FROM product
WHERE 1=1
	AND pdDeleteNy = #{shDelNy}


		AND pdCreationDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND pdModificationDate BETWEEN #{shDateStart} AND #{shDateEnd}



		AND pdSeq = #{shValue}
		AND pdName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE product 
SET
	pdName = #{pdName}
WHERE 1=1
	AND pdSeq = #{pdSeq}

mapper.delete:
DELETE FROM product
WHERE 1=1
	AND pdSeq = #{pdSeq}
